import React, { useEffect, useState } from 'react';

import Tasks from './components/Tasks/Tasks';
import NewTask from './components/NewTask/NewTask';
import useHttp from './hooks/use-http';

function App() {
  const [tasks, setTasks] = useState([]);

  const config = {
    url: 'https://react-practice-43e51-default-rtdb.europe-west1.firebasedatabase.app/tasks.json',
  }

  const { isLoading, error, sendRequest: fetchTasks } = useHttp();

  useEffect(() => {
    const transformTasks = (taskObject) => {
    const loadedTasks = [];

    for (const taskKey in taskObject){
      loadedTasks.push({ id: taskKey, text: taskObject[taskKey].text });
    }
    setTasks(loadedTasks);
  };

    fetchTasks(
      { url: 'https://react-practice-43e51-default-rtdb.europe-west1.firebasedatabase.app/tasks.json'},
      transformTasks);
  }, [fetchTasks]);

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };

  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  );
}

export default App;
